const templateCommand =
  '##################################\n' +
  '# This is an autogenerated command stub\n' +
  '# Command will not be executed directly when "pipeline" attribute is present in the "runtime" section\n' +
  '# Corresponding pipeline/version will be launched instead\n' +
  '##################################\n' +
  '# Pipeline: {PIPELINE_NAME}\n' +
  '# Version: {PIPELINE_VERSION}\n' +
  '##################################\n' +
  '{PARAMS}\n';

export default function (pipelineName, version, inputs) {
  const inputsString = (inputs || []).map(i => `# PARAM ${i}`).join('\n');
  return templateCommand
    .replace(/\{PIPELINE_NAME\}/ig, pipelineName || '')
    .replace(/\{PIPELINE_VERSION\}/ig, version)
    .replace(/\{PARAMS\}/ig, inputsString);
}
